function setAccordionEvents() {
  var b = document.getElementsByClassName("accordion-header"),
    a;
  for (a = 0; a < b.length; a++)
    b[a].addEventListener("click", function () {
      this.classList.toggle("active");
      var a = this.nextElementSibling;
      a.style.maxHeight && "" != a.style.maxHeight
        ? (a.style.maxHeight = null)
        : (settings.allowAccordionClick && closeAllAccordions(),
          (a.style.maxHeight = a.scrollHeight + "px"));
    });
}
function setAccordionItemEvents() {
  var b = document.getElementsByClassName("accordion-item"),
    a;
  for (a = 0; a < b.length; a++)
    b[a].addEventListener("click", function () {
      index = getIndexByID(this.dataset.id);
      snippetToScreen();
      saveIndex();
      closeNav();
    });
}
function closeAllAccordions() {
  var b = document.getElementsByClassName("panel"),
    a;
  for (a = 0; a < b.length; a++) b[a].style.maxHeight = null;
}
