function showModal(a) {
  document.getElementById(a).style.display = "block";
}
function hideModal(a) {
  document.getElementById(a).style.display = "none";
}
window.onclick = function (a) {
  settings.allowModalBGClick &&
    (("settings-modal" != a.target.id && "add-snippet-modal" != a.target.id) ||
      hideModal(a.target.id));
};
if ("function" === typeof isExtension && isExtension()) {
  var modals = document.getElementsByClassName("modal"),
    i;
  for (i = 0; i < modals.length; i++) modals[i].style.paddingTop = "10px";
}
