function showPage(c, d) {
  var a;
  a = document.getElementsByClassName("tabcontent");
  for (var b = 0; b < a.length; b++) a[b].style.display = "none";
  a = document.getElementsByClassName("tablinks");
  for (b = 0; b < a.length; b++)
    a[b].className = a[b].className.replace(" active", "");
  a = document.getElementById("settings-modal-footer");
  "settings" == d.toLowerCase()
    ? (a.style.display = "block")
    : (a.style.display = "none");
  document.getElementById(d).style.display = "block";
  c.currentTarget.className += " active";
}
