var MSG_HEADER_FONT = "Verdana, sans-serif",
  MSG_CONTENT_FONT = "Verdana, sans-serif",
  MSG_SUCCESS_COLOUR = "#4caf50",
  MSG_INFO_COLOUR = "#2196F3",
  MSG_WARNING_COLOUR = "#ff9800",
  MSG_ERROR_COLOUR = "#d32f2f";
function mainMsg() {
  return document.getElementById("msg-main");
}
function buildMsg() {
  var f = document.createElement("div"),
    a = document.createElement("div"),
    g = document.createElement("div"),
    b = document.createElement("i"),
    d = document.createElement("h2"),
    c = document.createElement("div"),
    h = document.createElement("div"),
    e = document.createElement("button"),
    k = document.createElement("i");
  f.style.display = "none";
  f.id = "msg-main";
  f.classList.add("msg-main");
  a.id = "msg-content";
  a.classList.add("msg-content");
  f.appendChild(a);
  g.id = "msg-header";
  g.classList.add("msg-header");
  a.appendChild(g);
  b.id = "msg-icon";
  b.classList.add("material-icons", "msg-icon");
  isExtension() && (b.style.paddingTop = "5px");
  g.appendChild(b);
  d.id = "msg-title";
  d.classList.add("msg-title");
  g.appendChild(d);
  c.id = "msg-body";
  c.classList.add("msg-body");
  a.appendChild(c);
  h.id = "msg-footer";
  h.classList.add("msg-footer");
  a.appendChild(h);
  e.id = "msg-close";
  e.classList.add("msg-close");
  e.innerHTML = "OK&nbsp;";
  e.onclick = function () {
    closeMsg();
  };
  h.appendChild(e);
  k.classList.add("material-icons");
  k.innerHTML = "check";
  e.appendChild(k);
  document.body.appendChild(f);
}
function showMsg(f, a) {
  if ("undefined" === typeof a || "" === a) return !1;
  buildMsg();
  var g = document.getElementById("msg-content"),
    b = document.getElementById("msg-icon"),
    d = document.getElementById("msg-title"),
    c = document.getElementById("msg-header"),
    h = document.getElementById("msg-footer"),
    e = document.getElementById("msg-body"),
    k = document.getElementById("msg-close");
  switch (f.toLowerCase()) {
    case "success":
      c.style.backgroundColor = MSG_SUCCESS_COLOUR;
      b.innerHTML = "check_circle";
      d.innerHTML = "Success!";
      break;
    case "info":
      c.style.backgroundColor = MSG_INFO_COLOUR;
      b.innerHTML = "info";
      d.innerHTML = "Info";
      break;
    case "warning":
      c.style.backgroundColor = MSG_WARNING_COLOUR;
      b.innerHTML = "warning";
      d.innerHTML = "Warning!";
      break;
    case "error":
      c.style.backgroundColor = MSG_ERROR_COLOUR;
      b.innerHTML = "error";
      d.innerHTML = "Error!";
      break;
    default:
      return !1;
  }
  c.style.fontFamily = MSG_HEADER_FONT;
  h.style.fontFamily = MSG_HEADER_FONT;
  k.style.fontFamily = MSG_HEADER_FONT;
  g.style.fontFamily = MSG_CONTENT_FONT;
  e.innerHTML = a;
  mainMsg().style.display = "block";
  return !0;
}
function closeMsg() {
  mainMsg().style.display = "none";
  mainMsg().remove();
}
